import { useState } from "react";

export default function App() {
  const [items, setItems] = useState([]);

  function handleAddItem(item) {
    setItems(items => [...items, item])
  }

  function deleleItem(id) {
    setItems(items.filter(item => item.id !== id))
  }

  function handleToggleItem(id) {
    setItems(items => items.map(item => item.id === id ? { ...item, packed: !item.packed } : item))
  }

  function handleClearList() {
    setItems = [];
  }
  return <div className="app">
    <Logo />
    <Form onAddItem={handleAddItem} />
    <PackingList items={items} onDeleteItem={deleleItem} onToggleItem={handleToggleItem} onClearList={handleClearList} />
    <Stats items={items} />
  </div>
}

function Logo() {
  return (
    <h1>🏝️ Far Away 🧳</h1>
  )
}

function Form({ onAddItem }) {
  const [description, setDescription] = useState("")
  const [quantity, setQuantity] = useState(5)
  function handleSubmit(e) {
    e.preventDefault()
    if (!description) return;
    const newItem = { quantity, description, id: Date.now(), packed: false }
    onAddItem(newItem)
    setQuantity(1)
    setDescription('')
  }
  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your 😍 trip ?</h3>
      <select value={quantity} onChange={e => setQuantity(+e.target.value)}>
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>{num}</option>
        ))}
      </select>
      <input value={description} onChange={e => setDescription(e.target.value)} type="text" placeholder="Item..." />
      <button>Add</button>
    </form>
  )
}

function PackingList({ items, onDeleteItem, onToggleItem, onClearList }) {
  const [sortBy, setSortBy] = useState('input');
  let sortItems;

  if (sortBy === 'input') sortItems = items;
  if (sortBy === 'description') sortItems = items.slice().sort((a, b) => a.description.localeCompare(b.description))
  if (sortBy === 'packed') sortItems = items.slice().sort((a, b) => Number(a.packed) - Number(b.packed))

  return (
    <div className="list">
      <ul>
        {sortItems.map(item => <Item item={item} key={item.id} onDeleteItem={onDeleteItem} onToggleItem={onToggleItem} />)}
      </ul>
      <div className="actions">
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="input">Sort by input order</option>
          <option value="description">Sort by description</option>
          <option value="packed">Sort by packed status</option>
        </select>
      </div>
      <button onClick={onClearList}>Clear List</button>
    </div>
  )
}

function Item({ item, onDeleteItem, onToggleItem }) {
  return (
    <li>
      <input type="checkbox" value={item.packed} onChange={() => onToggleItem(item.id)} />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>{item.quantity} {item.description}</span>
      <button onClick={() => onDeleteItem(item.id)}>❌</button>

    </li>
  )
}

function Stats({ items }) {
  if (items.length === 0) return (
    <p className="stats">
      <em>Start adding some item to your package list 🚀</em>
    </p>
  )
  const numItems = items.length;
  const packed = items.filter(item => item.packed).length;
  const percentage = Math.round(packed / numItems * 100)
  return (
    <footer className="stats">
      <em>{percentage === 100 ? "You got every thing! Ready to go ✈️" : `🧳 You have ${numItems} items on your list, and you already packed ${packed} (${percentage}%)`}
      </em>
    </footer>
  )
}
