import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 3, description: "Socks", quantity: 12, packed: false },
  { id: 4, description: "Socks", quantity: 12, packed: false },
  { id: 5, description: "Socks", quantity: 12, packed: false },
];

export default function App() {
  const [items, setItems] = useState([]);

  function handleAddItem(item) {
    setItems(items => [...items, item])
  }
  return <div className="app">
    <Logo />
    <Form onAddItem={handleAddItem} />
    <PackingList items={items} />
    <Stats />
  </div>
}

function Logo() {
  return (
    <h1>üèùÔ∏è Far Away üß≥</h1>
  )
}

function Form({ onAddItem }) {
  const [description, setDescription] = useState("")
  const [quantity, setQuantity] = useState(5)
  function handleSubmit(e) {
    e.preventDefault()
    if (!description) return;
    const newItem = { quantity, description, id: Date.now(), packed: false }
    onAddItem(newItem)
    setQuantity(1)
    setDescription('')
  }
  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your üòç trip ?</h3>
      <select value={quantity} onChange={e => setQuantity(+e.target.value)}>
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>{num}</option>
        ))}
      </select>
      <input value={description} onChange={e => setDescription(e.target.value)} type="text" placeholder="Item..." />
      <button>Add</button>
    </form>
  )
}

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map(item => <Item item={item} key={item.id} />)}
      </ul>
    </div>
  )
}

function Item({ item }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-throught" } : {}}>{item.quantity} {item.description}</span>
      <button>‚ùå</button>
    </li>
  )
}

function Stats() {
  return (
    <footer className="stats">
      <em>üß≥ You have X items on your list, and you already packed X (X%)</em>
    </footer>
  )
}
